package Questions;

import java.util.*;

public class AplusB {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 Scanner in = new Scanner(System.in);

	        int N = in.nextInt();
	        for (int i = 0; i < N; i++) {
	            int a = in.nextInt();
	            int b = in.nextInt();
	            System.out.println(a + b);
	        }
	}

}
